import React, { useReducer, useState } from 'react'; // Added import
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { login } from './slice';
import './LoginComp.css';


export default function LoginComp() {
  const init = {
    uid: "",
    pwd: ""
  };

  const reducer = (state, action) => {
    switch (action.type) {
      case 'update':
        return { ...state, [action.fld]: action.val };
      case 'reset':
        return init;
      default:
        return state;
    }
  };

  const [info, dispatch] = useReducer(reducer, init);
  const [msg, setMsg] = useState("");
  const navigate = useNavigate();
  const reduxAction = useDispatch();
  const mystate = useSelector((state) => state.logged);

  const handleChange = (e, field) => {
    dispatch({ type: 'update', fld: field, val: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Your login submission logic here
  };

  const handleClear = () => {
    dispatch({ type: 'reset' });
  };

  const sendData = (e) => {
    e.preventDefault();
    const reqOptions = {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({
        username: info.uid,
        password: info.pwd
      })
    };

    fetch("http://localhost:8080/checkUser", reqOptions)
      .then(resp => {
        if (resp.ok) {
          return resp.json();
        } else {
          throw new Error("Server Error");
        }
      })
      .then(obj => {
        if (obj === null) {
          setMsg("Wrong UID/PWD");
        } else {
          reduxAction(login());
          if (obj.activation_status === false) {
            alert("Request has not been approved");
          } else {
            switch (obj.role.role_id) {
              case 1:
                navigate("/admin_home");
                reduxAction(login());
                break;
              case 2:
                navigate("/consumer_home");
                reduxAction(login());
                break;
              case 3:
                navigate("/seller_home");
                reduxAction(login());
                break;
              default:
                break;
            }
          }
        }
      })
      .catch((error) => alert("Error : " + error.toString()));
  };

