import React, { useState, useEffect } from 'react';

function AnimatedHeading() {
  const [backgroundColor, setBackgroundColor] = useState('#f0f0f1');

  useEffect(() => {
    const interval = setInterval(() => {
      setBackgroundColor('#f0f0f1'); // Set the initial color
      setTimeout(() => {
        setBackgroundColor('#f1f0f0'); 
      }, 1000); // Adjust the delay here (e.g., 1000ms = 1 second)
    }, 2000); // Adjust the speed here (e.g., 2000ms = 2 seconds)

    return () => clearInterval(interval); // Cleanup
  }, []);

  return (
    <h1 style={{ backgroundColor, padding: '10px' }}>Welcome to Tritron</h1>
  );
}

export default AnimatedHeading;
