import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'; 

const ViewProducts = () => {
  const [products, setProducts] = useState([]);
  // const [searchCriteria, setSearchCriteria] = useState('category');
  // const [searchTerm, setSearchTerm] = useState('');

  const handleDelete = (id) => {
    console.log("Delete clicked for product with ID:", id);
    fetch(`http://localhost:8080/deleteProduct?id=${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Data:", data);
      })
      .catch((error) =>
        console.error("Error fetching data before delete:", error)
      );
  };
  

  // const handleSearch = () => {
  //   console.log(`Searching by ${searchCriteria}: ${searchTerm}`);

    
  // };

  useEffect(() => {
    axios.get('http://localhost:8080/viewProducts')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error fetching product data:', error);
      });
  }, []);

  return (
    <div className="container mt-4">
      <h2 className="mb-4" style={{ color: '#007BFF' }}>Products</h2>
      <div className="mb-3 row">
        {/* <div className="col-md-3">
          <select
            className="form-select"
            value={searchCriteria}
            onChange={(e) => setSearchCriteria(e.target.value)}
          >
            <option value="category">Category</option>
            <option value="brandName">Brand Name</option>
            <option value="modelName">Model Name</option>
          </select>
        </div> */}
        {/* <div className="col-md-6">
          <input
            type="text"
            className="form-control"
            placeholder={`Search by ${searchCriteria}`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div> */}
        {/* <div className="col-md-3">
          <button className="btn btn-primary" onClick={handleSearch}>
            Search
          </button>
        </div> */}
      </div>
      <table className="table table-striped table-hover">
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Category</th>
            <th>Brand Name</th>
            <th>Model Name</th>
            <th>Description</th>
            <th>Base Price</th>
            <th>Discounted Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.category}</td>
              <td>{product.brandName}</td>
              <td>{product.modelName}</td>
              <td>{product.description}</td>
              <td>{product.basePrice}</td>
              <td>{product.discountedPrice}</td>
              <td>
                <button className="btn btn-danger" onClick={() => handleDelete(product.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewProducts;
