import React, { useState } from 'react';
import './AddToCart.css'; // Import the CSS file

const AddToCart = ({ productName, models, onAddToCart }) => {
  const [quantity, setQuantity] = useState(1);
  const [brand, setBrand] = useState('');
  const [selectedModel, setSelectedModel] = useState('');

  const handleBrandChange = (event) => {
    setBrand(event.target.value);
  };

  const handleModelChange = (event) => {
    setSelectedModel(event.target.value);
  };

  const handleQuantityChange = (event) => {
    const newQuantity = parseInt(event.target.value);
    setQuantity(newQuantity);
  };

  const handleAddToCart = () => {
    onAddToCart(productName, brand, selectedModel, quantity);
  };

  return (
    <div className="add-to-cart-container">
      <div className="product-details">
        <h3>{productName}</h3>
      </div>
      <div className="brand-selector">
        <label htmlFor="brand">Brand:</label>
        <input
          type="text"
          id="brand"
          value={brand}
          onChange={handleBrandChange}
        />
      </div>
      <div className="model-selector">
        <label htmlFor="model">Model:</label>
        <select id="model" value={selectedModel} onChange={handleModelChange}>
          <option value="">Select Model</option>
          {models.map((model, index) => (
            <option key={index} value={model}>{model}</option>
          ))}
        </select>
      </div>
      <div className="quantity-selector">
        <label htmlFor="quantity">Quantity:</label>
        <input
          type="number"
          id="quantity"
          value={quantity}
          min="1"
          onChange={handleQuantityChange}
        />
      </div>
      <button className="add-to-cart-button" onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
};

export default AddToCart;
