import React, { useState, useEffect } from 'react';

const SearchProducts = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProducts(); // Fetch products from REST API when component mounts
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://example.com/api/products'); // Replace with your API endpoint
      if (!response.ok) {
        throw new Error('Failed to fetch products');
      }
      const data = await response.json();
      setProducts(data);
      setLoading(false);
    } catch (error) {
      console.error(error);
      setLoading(false);
    }
  };

  const handleSelectChange = (event) => {
    const selectedOptions = Array.from(event.target.selectedOptions, (option) => option.value);
    setSelectedProducts(selectedOptions);
  };

  const handleButtonClick = () => {
    // Display selected products in console (you can modify this according to your requirements)
    console.log('Selected Products:', selectedProducts);
  };

  return (
    <div>
      <h2>Search Products</h2>
      {loading ? (
        <p>Loading products...</p>
      ) : (
        <div>
          <label htmlFor="products">Select Products:</label>
          <select id="products" multiple onChange={handleSelectChange}>
            {products.map((product) => (
              <option key={product.id} value={product.id}>
                {product.name}
              </option>
            ))}
          </select>
          <br />
          <button onClick={handleButtonClick}>Show Selected Products</button>
          <br />
          {selectedProducts.length > 0 && (
            <div>
              <h3>Selected Products:</h3>
              <ul>
                {selectedProducts.map((productId) => (
                  <li key={productId}>{products.find((product) => product.id === productId)?.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchProducts;
